-module(day1_test).
-include_lib("eunit/include/eunit.hrl").
-import(day1, [get_floor/1, get_position/1, get_position_v2/1]).

get_floor_examples_test () ->
  Examples = #{
    "(())" => 0,
    "()()" => 0,
    "(((" => 3,
    "(()(()(" => 3,
    "))(((((" => 3,
    "())" => -1,
    "))(" => -1,
    ")))" => -3,
    ")())())" => -3
  },
  maps:foreach(
    fun(Instruction, ExpectedFloor) ->
      test_floor_examples({Instruction, ExpectedFloor})
    end, 
    Examples).


test_floor_examples({Instruction, ExpectedFloor}) ->
  ResultFloor = get_floor(Instruction),
  ?assertEqual(ExpectedFloor, ResultFloor).

get_floor_puzzle_input_test() -> 
  ResultFloor = get_floor(get_puzzle_input()),
  ?assertEqual(280, ResultFloor).

get_position_examples_test () ->
  Examples = #{
    ")" => 1,
    "()())" => 5
  },
  maps:foreach(
    fun(Instruction, ExpectedPosition) ->
      test_position_examples({Instruction, ExpectedPosition})
    end,
    Examples).

test_position_examples({Instruction, ExpectedPosition}) ->
  ResultPosition = get_position(Instruction),
  ?assertEqual(ExpectedPosition, ResultPosition).

get_position_puzzle_input_test() ->
  ResultPosition = get_position(get_puzzle_input()),
  ?assertEqual(1797, ResultPosition),
  ?debugTime("get_position", get_position(get_puzzle_input())).

get_position_v2_examples_test () ->
  Examples = #{
    ")" => 1,
    "()())" => 5
  },
  maps:foreach(
    fun(Instruction, ExpectedPosition) ->
      test_position_v2_examples({Instruction, ExpectedPosition})
    end,
    Examples).

test_position_v2_examples({Instruction, ExpectedPosition}) ->
  ResultPosition = get_position(Instruction),
  ?assertEqual(ExpectedPosition, ResultPosition).

get_position_v2_puzzle_input_test() ->
  ResultPosition = get_position_v2(get_puzzle_input()),
  ?assertEqual(1797, ResultPosition),
  ?debugTime("get_position", get_position_v2(get_puzzle_input())).
  
  

