defmodule Day1 do
  @moduledoc false
  alias Day1

  # With pattern matching and pipe!
  @spec get_floor(instruction :: String.t(), current_floor :: number) :: number
  def get_floor(instruction, current_floor \\ 0) do
    String.split(instruction, "", trim: true) |>
      Enum.reduce(current_floor, fn paren, floor ->
        case paren do
          "(" -> floor + 1
          ")" -> floor - 1
          _ -> floor
        end
      end)
  end

  # Utilizing substring
  @spec get_position_to_first_enter_basement(instruction :: String.t()) :: number
  def get_position_to_first_enter_basement(instruction) do
    String.split(instruction, "", trim: true) |>
      Enum.with_index |>
        Enum.reduce_while(0, fn {_, index}, current_position ->
          if get_floor(String.slice(instruction, 0..index)) == -1 do
            {:halt, index + 1}
          else
            {:cont, current_position}
          end
        end)
  end

  # Utilizing optionall current_floor param of get_floor so that it does not go through the whole substring
  @spec get_position_to_first_enter_basement_v2(instruction :: String.t()) :: number
  def get_position_to_first_enter_basement_v2(instruction) do
    String.split(instruction, "", trim: true) |>
      Enum.with_index |>
        Enum.reduce_while(0, fn {paren, index}, current_floor ->
          floor = get_floor(paren, current_floor)
          case floor do
            -1 -> {:halt, index + 1} # returns the current position / index instead of floor starting with 1
            _ -> {:cont, floor} # saves floor to use next iteration
          end
        end)
  end

  # Eliminate step converting array of characters to with index
  @spec get_position_to_first_enter_basement_v3(instruction :: String.t()) :: number
  def get_position_to_first_enter_basement_v3(instruction) do
    String.split(instruction, "", trim: true) |>
      Enum.reduce_while({0, 0}, fn paren, {current_floor, current_position} ->
        floor = get_floor(paren, current_floor)
        case floor do
          -1 -> {:halt, current_position + 1}
          _ -> {:cont, {floor, current_position + 1}}
        end
      end)
  end

