pub fn get_floor(instruction: &str, current_floor: i32) -> i32 {
  let mut floor = current_floor;

  for paren in instruction.chars() {
    if paren == '(' {
      floor += 1;
    }

    if paren == ')' {
      floor -= 1;
    } 
  }
  
  return floor;
}

pub fn get_position_to_first_enter_basement(instruction: &str) -> i32 {
  let mut position: i32 = 0;
  let mut current_floor: i32 = 0;

  for (index, paren) in instruction.chars().enumerate() {
    let floor = get_floor(paren.to_string().as_str(), current_floor);

    if floor == -1 {
      position = index as i32 + 1;
      break;
    }

    current_floor = floor
  }

  return position;
}

pub fn get_position_to_first_enter_basement_v2(instruction: &str) -> i32 {
  let mut position: i32 = 0;

  for (index, _) in instruction.chars().enumerate() {
    let incremented_index = index + 1;

    let floor = get_floor(&instruction[0..incremented_index as usize], 0);

    if floor == -1 {
      position = incremented_index as i32;
      break;
    }
  }

  return position;
}

#[cfg(test)]
mod tests {
  use super::*;
  use std::collections::HashMap;

	#[test]
  fn test_get_floor () {
    let examples: HashMap<String, i32> =  vec![
      ("(())".to_string(), 0),
      ("()()".to_string(), 0),
      ("(((".to_string(), 3),
      ("(()(()(".to_string(), 3),
      ("))(((((".to_string(), 3),
      ("())".to_string(), -1),
      ("))(".to_string(), -1),
      (")))".to_string(), -3),
      (")())())".to_string(), -3),
    ]
    .into_iter()
    .collect();

    let mut result_floor;

    for (instruction, expected_floor) in &examples {
      result_floor = get_floor(instruction, 0);
  
      assert_eq!(result_floor, *expected_floor);
    }

    let puzzle_input: &str = PUZZLE_INPUT;

    result_floor = get_floor(puzzle_input, 0);
    assert_eq!(result_floor, 280)
  }

  #[test]
  fn test_get_position_to_first_enter_basement() {
    let examples: HashMap<String, i32> = vec![
      (")".to_string(), 1),
      ("()())".to_string(), 5),
    ]
    .into_iter()
    .collect();

    let mut result_position;

    for (instruction, expected_position) in &examples {
      result_position = get_position_to_first_enter_basement(instruction);

      assert_eq!(result_position, *expected_position)
    }

    result_position = get_position_to_first_enter_basement(PUZZLE_INPUT);
    assert_eq!(result_position, 1797)
  }

  #[test]
  fn test_get_position_to_first_enter_basement_v2() {
    let examples: HashMap<String, i32> = vec![
      (")".to_string(), 1),
      ("()())".to_string(), 5),
    ]
    .into_iter()
    .collect();

    let mut result_position;

    for (instruction, expected_position) in &examples {
      result_position = get_position_to_first_enter_basement_v2(instruction);

      assert_eq!(result_position, *expected_position)
    }

    result_position = get_position_to_first_enter_basement_v2(PUZZLE_INPUT);
    assert_eq!(result_position, 1797)
  }
}

