// REVIEW: how to destructure imports?
const std = @import("std");
const StringHashMap = std.StringHashMap;
const testing = std.testing;
const allocator = std.testing.allocator;
const expect = std.testing.expect;

const day1 = @import("day1.zig");
const getFloor = day1.getFloor;
const getPosition = day1.getPosition;

test "getFloor" {
    var examples: StringHashMap(i32) = StringHashMap(i32).init(allocator);
    defer examples.deinit();

    try examples.put("(())", 0); // TODO: Find a way to literally declare this in the instantiation.
    try examples.put("()()", 0);
    try examples.put("(((", 3);
    try examples.put("(()(()(", 3);
    try examples.put("))(((((", 3);
    try examples.put("())", -1);
    try examples.put("))(", -1);
    try examples.put(")))", -3);
    try examples.put(")())())", -3);
    try examples.put(puzzle_input, 280);

    // REVIEW: Can hashmaps be used in for loop?
    var it = examples.iterator();
    while (it.next()) |example| {
        var instruction = example.key_ptr.*;
        var expected_floor = example.value_ptr.*;

        var result_floor = getFloor(instruction);
        // std.debug.print("\n{d} {d}\n", .{ result_floor, expected_floor });

        try expect(result_floor == expected_floor);
    }
}

