test "getPosition" {
    var examples: StringHashMap(i32) = StringHashMap(i32).init(allocator);
    defer examples.deinit();

    try examples.put(")", 1); // TODO: Find a way to literally declare this in the instantiation.
    try examples.put("()())", 5);
    try examples.put(puzzle_input, 1797);

    // NOTE: Can hashmaps be used in for loop?
    var it = examples.iterator();
    while (it.next()) |example| {
        var instruction = example.key_ptr.*;
        var expected_position = example.value_ptr.*;

        var result_position = getPosition(instruction);

        try expect(result_position == expected_position);
    }
}

