from day1 import get_floor, get_position_to_first_enter_basement, get_position_to_first_enter_basement_v2

def test_get_floor ():
  examples = {
    '(())': 0,
    '()()': 0,
    '(((': 3,
    '(()(()(': 3,
    '))(((((': 3,
    '())': -1,
    '))(': -1,
    ')))': -3,
    ')())())': -3,
  }

  for instruction, expected_floor in examples.items():
    result_floor = get_floor(instruction)
    assert result_floor == expected_floor
  

  result_floor = get_floor(puzzle_input)
  assert result_floor == 280

examples = {
  ')': 1,
  '()())': 5,
}

def test_get_position_to_first_enter_basement (benchmark):
  for instruction, expected_position in examples.items():
    result_position = get_position_to_first_enter_basement(instruction)
    assert result_position == expected_position

  result_position = benchmark(get_position_to_first_enter_basement, puzzle_input)
  assert result_position == 1797

def test_get_position_to_first_enter_basement_v2 (benchmark):  
  for instruction, expected_position in examples.items():
    result_position = get_position_to_first_enter_basement_v2(instruction)
    assert result_position == expected_position

  result_position = benchmark(get_position_to_first_enter_basement_v2, puzzle_input)
  assert result_position == 1797




