

def get_floor(instruction, current_floor = 0)
  parens = instruction.split('')

  floor = current_floor

  parens.each { |paren|
    if paren == '(' 
      floor += 1
    elsif paren == ')'
      floor -= 1
    end
  }

  floor
end

def get_floor_v2(instruction, current_floor = 0)
  floor = current_floor

  instruction.each_char { |paren|
    if paren == '(' 
      floor += 1
    elsif paren == ')'
      floor -= 1
    end
  }

  floor
end

# Index starts at 0 and is only up to length - 1, since we are using it as an index of the current parens
def get_position_to_first_enter_basement(instruction)
  position = 0
  current_floor = 0

  for i in 0..instruction.length - 1
    floor = get_floor(instruction[i], current_floor)

    if floor == -1
      position = i + 1
      break
    end

    current_floor = floor
  end

  position
end

# Index starts with 0 and up to length. We can't use the same implem in v2 implementation in Go where in we start with 1, since the index is not being used as an upper bound but still, an index
def get_position_to_first_enter_basement_v2(instruction)
  position = 0

  for i in 0..instruction.length-1
    floor = get_floor(instruction[0..i])

    if floor == -1
      position = i + 1
      break
    end
  end

  position
end

